import { Component } from '@angular/core';
import { Cell } from '../models/Cell';
import { CellService } from '../services/Cell.service';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent {

  rows: number = 8;
  columns: number = 8;
  numberOfMines: number = 10;
  board: Cell[][] = [];

  constructor(private cellService: CellService){
    this.board = cellService.buildBoard(this.rows, this.columns, this.numberOfMines);  
  }


  checkCell(cell: Cell, row: number, column: number){
    this.cellService.checkCell(cell);
    // console.log(cell, row, column);
    this.cellService.openCell(this.board, row, column, this.rows, this.columns);
  }



}
