import { Cell } from "../models/Cell";

export class CellService{

    buildBoard(rows: number, columns: number){

        let board: Cell[][] = []
        for(let i = 0; i < rows; i++){
            let rows: Cell[] = [];
            for(let j = 0; j < columns; j++){
              let element: Cell = {status: 'toOpen', mine: false, mineProximity: 0};
              rows.push(element);
            }
            board.push(rows);
        }


        board = this.putMines(2, board, rows, columns);

        return board;
    }


    putMines(numberOfMines: number, board: Cell[][], rows: number, columns: number){
        let set = new Set<([number, number])>;

        for(let i = 0; i < numberOfMines; i++){
            let pairs: [number, number] = [Math.floor(Math.random() * (rows)), Math.floor(Math.random() * (columns))];

            if(set.has(pairs)){
                i--;
            }
            else{
                set.add(pairs);
            }
        }

        

        for(let pair of set){
            console.log(pair[0], pair[1])
            board[pair[0]][pair[1]].mine = true;
        }

        return board;
    }


}
