import { Component } from '@angular/core';
import { Cell } from '../models/Cell';
import { CellService } from '../services/Cell.service';
import { GameState } from '../models/Game.enum';


@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent {

  rows: number = 8;
  columns: number = 8;
  numberOfMines: number = 1;
  gameState: GameState = GameState.playing;
  dificulty: string = "Easy";
  cellsGame: any = {cellsOpened: 0, cells: this.rows * this.columns, numberMines: this.numberOfMines};
  board: Cell[][] = [];

  constructor(private cellService: CellService){
    if(this.dificulty == "Easy"){
      this.rows = this.columns = 8;
      this.numberOfMines = 10;
    }
    else if(this.dificulty == "Normal"){
      this.rows = this.columns = 13;
      this.numberOfMines = 20;
    }
    else if(this.dificulty == "Hard"){
      this.rows = this.columns = 15;
      this.numberOfMines = 40;
    }
    else{
      this.rows = this.columns = 15;
      this.numberOfMines = 90;
    }
    this.board = cellService.buildBoard(this.rows, this.columns, this.numberOfMines);
    console.log(this.dificulty);  
  }


  checkCell(row: number, column: number){
    // this.gameState = this.cellService.checkCell(this.board, row, column, this.cellsGame, this.numberOfMines, this.gameState);
    this.cellService.openCell(this.board, row, column, this.rows, this.columns, this.cellsGame);
    this.gameState = this.cellService.checkCell(this.board, row, column, this.cellsGame, this.numberOfMines, this.gameState);
    // console.log("Estado del juego ", this.gameState)

    // console.log(GameState.lose);
    // console.log(GameState.playing);
    // console.log(GameState.win);
  }

  flag(cell: Cell){
    this.cellService.flag(cell);

    return false;
  }


  reset(){
    this.board = this.cellService.buildBoard(this.rows, this.columns, this.numberOfMines);
    this.gameState = GameState.playing;
    this.cellsGame = {cellsOpened: 0, cells: this.rows * this.columns, numberMines: this.numberOfMines};
  }


}

<p>Escoge la dificultad</p>
<select [(ngModel)]="dificulty">
    <option value="Easy">Easy</option>
    <option value="Normal">Normal</option>
    <option value="Hard">Hard</option>
    <option value="Extreme">Extreme</option>
</select>

<p>{{ dificulty }}</p>

<div *ngIf="gameState == 1 || gameState == 0 || gameState == 2">
    <table class="Game">
        <tr *ngFor="let rows of board; index as y;">
            <td *ngFor="let cell of rows; index as x;" (click)="gameState == 1 && checkCell(y, x)" (contextmenu)="gameState == 1 && flag(cell)">
                <div *ngIf="cell.status =='open'" class="celdas">
                    {{cell.mineProximity}}
                </div>

                <div *ngIf="cell.status == 'mine'" class="mine">
                    <svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" viewBox="0 0 24 24"><path fill="#cb2a2a" d="M23 13v-2h-3.07a8 8 0 0 0-1.62-3.9l2.19-2.17l-1.43-1.43l-2.17 2.19A8 8 0 0 0 13 4.07V1h-2v3.07c-1.42.18-2.77.74-3.9 1.62L4.93 3.5L3.5 4.93L5.69 7.1A8 8 0 0 0 4.07 11H1v2h3.07c.18 1.42.74 2.77 1.62 3.9L3.5 19.07l1.43 1.43l2.17-2.19c1.13.88 2.48 1.44 3.9 1.62V23h2v-3.07c1.42-.18 2.77-.74 3.9-1.62l2.17 2.19l1.43-1.43l-2.19-2.17a8 8 0 0 0 1.62-3.9zM12 8a4 4 0 0 0-4 4H6a6 6 0 0 1 6-6z"/></svg>
                </div>


                <div *ngIf="cell.status == 'flag'">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>flag</title><path d="M14.4,6L14,4H5V21H7V14H12.6L13,16H20V6H14.4Z" /></svg>
                </div>
            </td>
        </tr>
    </table>
</div>

<div *ngIf="gameState == 2" class="Win">
    <div>
        <img src="../../assets/celebration.jpg" alt="Win" class="imgWin">
    </div>

    <h3>Felicidades. Has Ganado!!!</h3>
</div>

<div *ngIf="gameState == 0" class="Lose">
    <br>
    <h3>Vuelve a Intentarlo</h3>
    <button (click)="reset()">Reset</button>
</div>
